package aula1;

public class case1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i1, i2, r1, r2, r3, r4;

		i1 = 8;
		i2 = 4;
		r1 = (i1+i2);
		r2 = (i1-i2);
		r3 = (i1*i2);
		r4 = (i1/i2);
		System.out.println ("Valor 1 = "+i1+", e valor 2 = "+i2);		
		System.out.println ("Soma: "+r1);
		System.out.println ("Subrtação: "+r2);	
		System.out.println ("Multiplicação: "+r3);
		System.out.println ("Divisão: "+r4);
	}

}
